import React from 'react';
import '../App.css';
import BreakInterval from "./BreakInterval";
import SessionLength from "./SessionLength";
import Timer from "./Timer";

class App extends React.Component {
      constructor() {
            super();

            this.state = {
                  breakLength: 5,
                  sessionLength: 25,
                  timerMinute: 25,
                  isPlay: false
            };
            this.onIncreseBreakLength = this.onIncreseBreakLength.bind(this);
            this.onDecreaseBreakLength = this.onDecreaseBreakLength.bind(this);
            this.onIncreaseSessionLength = this.onIncreaseSessionLength.bind(this);
            this.onDecreaseSessionLength = this.onDecreaseSessionLength.bind(this);
            this.onUpdateTimerMinute = this.onUpdateTimerMinute.bind(this);
            this.onToggleInterval = this.onToggleInterval.bind(this);
            this.onPlayer = this.onPlayer.bind(this);
            this.onReset = this.onReset.bind(this);
      }
      onIncreseBreakLength() {
            this.setState((prevState) => {
                  return {
                        breakLength: prevState.breakLength + 1
                  }
            })
      }
      onDecreaseBreakLength() {
            this.setState((prevState) => {
                  return {
                        breakLength: prevState.breakLength - 1
                  }
            })
      }
      onIncreaseSessionLength() {
            this.setState((prevState) => {
                  return {
                        sessionLength: prevState.sessionLength + 1,
                        timerMinute: prevState.sessionLength + 1
                  }
            })
      }
      onDecreaseSessionLength() {
            this.setState((prevState) => {
                  return {
                        sessionLength: prevState.sessionLength - 1,
                        timerMinute: prevState.sessionLength - 1
                  }
            })
      }
      onUpdateTimerMinute() {
            this.setState((prevState) => {
                  return {
                        timerMinute: prevState.timerMinute - 1
                  }
            })
      }
      onToggleInterval(isSession) {
            if (isSession) {
                  this.setState({
                        timerMinute: this.state.sessionLength
                  })
            } else {
                  this.setState({
                        timerMinute: this.state.breakLength
                  })
            }
      }
      onReset() {
            this.setState({
                  timerMinute: this.state.sessionLength
            })
      }
      onPlayer(isPlay) {
            this.setState({
                  isPlay: isPlay
            })
      }
      render() {
            return (
                  <div className="App" >
                        <main>
                              <h2> Minuteur Pomodoro  </h2>
                              <section className="interval-length-container">
                                    <BreakInterval
                                          isPlay={this.state.isPlay}
                                          breakInterval={this.state.breakLength}
                                          increaseBreak={this.onIncreseBreakLength}
                                          decreaseBreak={this.onDecreaseBreakLength}
                                    />
                                    <SessionLength
                                          isPlay={this.state.isPlay}
                                          sessionLength={this.state.sessionLength}
                                          increaseSession={this.onIncreaseSessionLength}
                                          decreaseSession={this.onDecreaseSessionLength} />

                              </section>
                              <Timer timerMinute={this.state.timerMinute}
                                    breakLength={this.state.breakLength}
                                    onUpdateTimerMinute={this.onUpdateTimerMinute}
                                    onToggleInterval={this.onToggleInterval}
                                    onReset={this.onReset}
                                    onPlayer={this.onPlayer}

                              />

                        </main>

                  </div>
            );
      }
}

export default App;
